mapper.select:
SELECT
    AVG(fieldValue) AS avgValue,
    DATETIME_CONVERT(eventTime, '1:MILLISECONDS:EPOCH', '1:MILLISECONDS:EPOCH', '#{timePrecision.timeSize}:${timePrecision.timeUnit}') AS avgTime
FROM inspectorStat
WHERE

    applicationName = #{applicationName}
    AND agentId = #{agentId}
    AND metricName = #{metricName}
    AND fieldName = #{fieldName}
    AND eventTime BETWEEN #{range.from} AND #{range.to}

        AND tags = #{tag}

    GROUP BY avgTime
    ORDER BY avgTime asc
    LIMIT ${limit}

SELECT
MAX(fieldValue) AS avgValue,
DATETIME_CONVERT(eventTime, '1:MILLISECONDS:EPOCH', '1:MILLISECONDS:EPOCH', '#{timePrecision.timeSize}:${timePrecision.timeUnit}') AS avgTime
FROM inspectorStat
WHERE

applicationName = #{applicationName}
AND agentId = #{agentId}
AND metricName = #{metricName}
AND fieldName = #{fieldName}
AND eventTime BETWEEN #{range.from} AND #{range.to}

    AND tags = #{tag}

GROUP BY avgTime
ORDER BY avgTime asc
LIMIT ${limit}

SELECT
SUM(fieldValue) AS avgValue,
DATETIME_CONVERT(eventTime, '1:MILLISECONDS:EPOCH', '1:MILLISECONDS:EPOCH', '#{timePrecision.timeSize}:${timePrecision.timeUnit}') AS avgTime
FROM inspectorStat
WHERE

applicationName = #{applicationName}
AND agentId = #{agentId}
AND metricName = #{metricName}
AND fieldName = #{fieldName}
AND eventTime BETWEEN #{range.from} AND #{range.to}

    AND tags = #{tag}

GROUP BY avgTime
ORDER BY avgTime asc
LIMIT ${limit}

SELECT DISTINCT(tags)
FROM inspectorStat
WHERE
    applicationName = #{applicationName}
    AND agentId = #{agentId}
    AND metricName = #{metricName}
    AND fieldName = #{fieldName}
    AND eventTime BETWEEN #{range.from} AND #{range.to}

SELECT applicationName, agentId, metricName, fieldName, tags
FROM inspectorStat
WHERE
    applicationName = #{applicationName}
    AND agentId = #{agentId}
    AND metricName = #{metricName}
    AND fieldName = #{fieldName}
    AND eventTime BETWEEN #{range.from} AND #{range.to}

        AND tags = #{tag}

LIMIT 1
