mapper.select:
SELECT
    AVG(fieldValue) AS avgValue,
    DATETIME_CONVERT(eventTime, '1:MILLISECONDS:EPOCH', '1:MILLISECONDS:EPOCH', '#{timePrecision.timeSize}:${timePrecision.timeUnit}') AS avgTime
FROM inspectorStat
WHERE

    agentId = #{agentId}
    AND metricName = #{metricName}
    AND fieldName = #{fieldName}

        AND tags = #{tag}

    AND eventTime BETWEEN #{range.from} AND #{range.to}
    GROUP BY avgTime
    ORDER BY avgTime asc
    LIMIT ${limit}

SELECT
MAX(fieldValue) AS avgValue,
DATETIME_CONVERT(eventTime, '1:MILLISECONDS:EPOCH', '1:MILLISECONDS:EPOCH', '#{timePrecision.timeSize}:${timePrecision.timeUnit}') AS avgTime
FROM inspectorStat
WHERE

agentId = #{agentId}
AND metricName = #{metricName}
AND fieldName = #{fieldName}

    AND tags = #{tag}

AND eventTime BETWEEN #{range.from} AND #{range.to}
GROUP BY avgTime
ORDER BY avgTime asc
LIMIT ${limit}

SELECT
SUM(fieldValue) AS avgValue,
DATETIME_CONVERT(eventTime, '1:MILLISECONDS:EPOCH', '1:MILLISECONDS:EPOCH', '#{timePrecision.timeSize}:${timePrecision.timeUnit}') AS avgTime
FROM inspectorStat
WHERE

agentId = #{agentId}
AND metricName = #{metricName}
AND fieldName = #{fieldName}

    AND tags = #{tag}

AND eventTime BETWEEN #{range.from} AND #{range.to}
GROUP BY avgTime
ORDER BY avgTime asc
LIMIT ${limit}

SELECT DISTINCT(tags)
FROM inspectorStat
WHERE
    agentId = #{agentId}
    AND metricName = #{metricName}
    AND fieldName = #{fieldName}
    AND eventTime BETWEEN #{range.from} AND #{range.to}

SELECT agentId, metricName, fieldName, tags
FROM inspectorStat
WHERE
    agentId = #{agentId}
    AND metricName = #{metricName}
    AND fieldName = #{fieldName}

        AND tags = #{tag}

    AND eventTime BETWEEN #{range.from} AND #{range.to}
LIMIT 1
