mapper.select:
SELECT
    metricName,
    hostName,
    fieldName,
    fieldValue,
    tags,
    timestampInEpoch
FROM systemMetricLong
WHERE applicationName = #{applicationName}
    AND metricName = #{metricName}
    AND hostName = #{hostName}
    AND fieldName = #{fieldName}

        AND tags = #{tag}

    AND timestampInEpoch BETWEEN #{range.from} AND #{range.to}
LIMIT ${limit}

SELECT
    AVG(fieldValue) AS avgValue,
    DATETIME_CONVERT(timestampInEpoch, '1:MILLISECONDS:EPOCH', '1:MILLISECONDS:EPOCH', '#{timePrecision.timeSize}:${timePrecision.timeUnit}') AS avgTime,
    tags
FROM systemMetricLong
WHERE applicationName = #{applicationName}
    AND metricName = #{metricName}
    AND hostName = #{hostName}
    AND fieldName = #{fieldName}

        AND tags = #{tag}

    AND timestampInEpoch BETWEEN #{range.from} AND #{range.to}
GROUP BY avgTime, tags
ORDER BY avgTime asc
LIMIT ${limit}
