mapper.select:
SELECT
    DISTINCT (metricGroupName)
FROM otlpMetricMetadata
WHERE serviceId= #{serviceId}
  AND applicationId= #{applicationId}

  AND agentId = #{agentId}

SELECT
    DISTINCT (metricName)
FROM otlpMetricMetadata
WHERE serviceId = #{serviceId}
    AND applicationId = #{applicationId}

    AND agentId = #{agentId}

AND metricGroupName = #{metricGroupName}

SELECT
    DISTINCT (rawTags)
FROM otlpMetricMetadata
WHERE serviceId = #{serviceId}
    AND applicationId = #{applicationId}

    AND agentId = #{agentId}

    AND metricGroupName = #{metricGroupName}
    AND metricName = #{metricName}

SELECT
    DISTINCT (fieldName),
    metricType,
    dataType,
    aggreFunc,
    aggreTemporality AS aggregationTemporality,
    description,
    unit,
    version
FROM otlpMetricMetadata
WHERE serviceId = #{serviceId}
    AND applicationId = #{applicationId}

    AND agentId = #{agentId}

    AND metricGroupName = #{metricGroupName}
    AND metricName = #{metricName}
    AND rawTags = #{rawTags}

SELECT

    AVG(value) as value


    SUM(value) as value


    MIN(value) as value


    MAX(value) as value


    LASTWITHTIME(value, eventTime, 'long') as value

FROM otlpMetricDouble
WHERE serviceId = #{serviceId}
AND applicationId = #{applicationId}

    AND agentId = #{agentId}

AND metricGroupName = #{metricGroupName}
AND metricName = #{metricName}
AND fieldName = #{fieldName}

AND tags = #{tag}

AND version = #{version}
AND eventTime BETWEEN #{range.from} AND #{range.to}
GROUP BY fieldName
LIMIT #{limit}

SELECT

AVG(value) as value


SUM(value) as value


MIN(value) as value


MAX(value) as value


LASTWITHTIME(value, eventTime, 'long') as value

FROM otlpMetricLong
WHERE serviceId = #{serviceId}
AND applicationId = #{applicationId}

AND agentId = #{agentId}

AND metricGroupName = #{metricGroupName}
AND metricName = #{metricName}
AND fieldName = #{fieldName}

AND tags = #{tag}
        AND version = #{version}
AND eventTime BETWEEN #{range.from} AND #{range.to}
GROUP BY fieldName
LIMIT #{limit}

SELECT
    DATETIME_CONVERT(eventTime, '1:MILLISECONDS:EPOCH', '1:MILLISECONDS:EPOCH', '#{timePrecision.timeSize}:${timePrecision.timeUnit}') as eventTime,
    version,

    AVG(value) as value


    SUM(value) as value


    MIN(value) as value


    MAX(value) as value


    LASTWITHTIME(value, eventTime, 'long') as value

FROM otlpMetricLong
WHERE serviceId = #{serviceId}
AND applicationId = #{applicationId}

    AND agentId = #{agentId}

AND metricGroupName = #{metricGroupName}
AND metricName = #{metricName}
AND fieldName = #{fieldName}

AND tags = #{tag}
        AND version = #{version}
AND eventTime BETWEEN #{range.from} AND #{range.to}
GROUP BY eventTime, version
ORDER BY eventTime asc
LIMIT #{limit}

SELECT
DATETIME_CONVERT(eventTime, '1:MILLISECONDS:EPOCH', '1:MILLISECONDS:EPOCH', '#{timePrecision.timeSize}:${timePrecision.timeUnit}') as eventTime,
version,

AVG(value) as value


SUM(value) as value


MIN(value) as value


MAX(value) as value


LASTWITHTIME(value, eventTime, 'double') as value

FROM otlpMetricDouble
WHERE serviceId = #{serviceId}
AND applicationId = #{applicationId}

AND agentId = #{agentId}

AND metricGroupName = #{metricGroupName}
AND metricName = #{metricName}
AND fieldName = #{fieldName}

AND tags = #{tag}

AND version = #{version}
AND eventTime BETWEEN #{range.from} AND #{range.to}
GROUP BY eventTime, version
ORDER BY eventTime asc
LIMIT #{limit}
